        almoemenonExes.add(new AlmoemenonEx("قَالَ رَبِّ انْصُرْنِي بِمَا كَذَّبُونِ (26)"));
        almoemenonExes.add(new AlmoemenonEx("قال نوح: رب انصرني على قومي; بسبب تكذيبهم إياي فيما بلَّغتهم من رسالتك."));
