        alshoaraaExes.add(new AlshoaraaEx("(طسم) سبق الكلام على الحروف المقطعة في أول سورة البقرة."));
        alshoaraaExes.add(new AlshoaraaEx("تِلْكَ آيَاتُ الْكِتَابِ الْمُبِينِ (2)"));
        alshoaraaExes.add(new AlshoaraaEx("هذه آيات القرآن الموضِّح لكل شيء الفاصل بين الهدى والضلال."));
        alshoaraaExes.add(new AlshoaraaEx("لَعَلَّكَ بَاخِعٌ نَفْسَكَ أَلَّا يَكُونُوا مُؤْمِنِينَ (3)"));
        alshoaraaExes.add(new AlshoaraaEx("لعلك - أيها الرسول - من شدة حرصك على هدايتهم مُهْلِك نفسك؛ لأنهم لم يصدِّقوا بك ولم يعملوا بهديك، فلا تفعل ذلك."));
        alshoaraaExes.add(new AlshoaraaEx("إِنْ نَشَأْ نُنَزِّلْ عَلَيْهِمْ مِنَ السَّمَاءِ آيَةً فَظَلَّتْ أَعْنَاقُهُمْ لَهَا خَاضِعِينَ (4)"));
        alshoaraaExes.add(new AlshoaraaEx("إن نشأ ننزل على المكذبين من قومك من السماء معجزة مخوِّفة لهم تلجئهم إلى الإيمان، فتصير أعناقهم خاضعة ذليلة، ولكننا لم نشأ ذلك; فإن الإيمان النافع هو الإيمان بالغيب اختيارًا."));
        alshoaraaExes.add(new AlshoaraaEx("وَمَا يَأْتِيهِمْ مِنْ ذِكْرٍ مِنَ الرَّحْمَنِ مُحْدَثٍ إِلَّا كَانُوا عَنْهُ مُعْرِضِينَ (5)"));
        alshoaraaExes.add(new AlshoaraaEx("وما يجيء هؤلاء المشركين المكذبين مِن ذِكْرٍ من الرحمن مُحْدَث إنزاله، شيئًا بعد شيء، يأمرهم وينهاهم، ويذكرهم بالدين الحق إلا أعرضوا عنه، ولم يقبلوه."));
        alshoaraaExes.add(new AlshoaraaEx("فَقَدْ كَذَّبُوا فَسَيَأْتِيهِمْ أَنْبَاءُ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ (6)"));
        alshoaraaExes.add(new AlshoaraaEx("فقد كذَّبوا بالقرآن واستهزؤوا به، فسيأتيهم أخبار الأمر الذي كانوا يستهزئون به ويسخرون منه، وسيحلُّ بهم العذاب جزاء تمردهم على ربهم."));
        alshoaraaExes.add(new AlshoaraaEx("أَوَلَمْ يَرَوْا إِلَى الْأَرْضِ كَمْ أَنْبَتْنَا فِيهَا مِنْ كُلِّ زَوْجٍ كَرِيمٍ (7)"));
        alshoaraaExes.add(new AlshoaraaEx("أكذبوا ولم ينظروا إلى الأرض التي أنبتنا فيها من كل نوع حسن نافع من النبات، لا يقدر على إنباته إلا رب العالمين؟"));
        alshoaraaExes.add(new AlshoaraaEx("إِنَّ فِي ذَلِكَ لَآيَةً وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ (8)"));
        alshoaraaExes.add(new AlshoaraaEx("إن في إخراج النبات من الأرض لَدلالة واضحة على كمال قدرة الله، وما كان أكثر القوم مؤمنين."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ (9)"));
        alshoaraaExes.add(new AlshoaraaEx("وإن ربك لهو العزيز على كل مخلوق، الرحيم الذي وسعت رحمته كل شيء."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِذْ نَادَى رَبُّكَ مُوسَى أَنِ ائْتِ الْقَوْمَ الظَّالِمِينَ (10)"));
        alshoaraaExes.add(new AlshoaraaEx("واذكر - أيها الرسول - لقومك إذ نادى ربك موسى: أن ائت القوم الظالمين."));
        alshoaraaExes.add(new AlshoaraaEx("قَوْمَ فِرْعَوْنَ أَلَا يَتَّقُونَ (11)"));
        alshoaraaExes.add(new AlshoaraaEx("قوم فرعون، وقل لهم: ألا يخافون عقاب الله تعالى، ويتركون ما هم عليه من الكفر والضلال؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ رَبِّ إِنِّي أَخَافُ أَنْ يُكَذِّبُونِ (12)"));
        alshoaraaExes.add(new AlshoaraaEx("قال موسى: رب إني أخاف أن يكذبوني في الرسالة، ويملأ صدري الغمُّ لتكذيبهم إياي."));
        alshoaraaExes.add(new AlshoaraaEx("وَيَضِيقُ صَدْرِي وَلَا يَنْطَلِقُ لِسَانِي فَأَرْسِلْ إِلَى هَارُونَ (13)"));
        alshoaraaExes.add(new AlshoaraaEx("ولا ينطلق لساني بالدعوة فأرسِلْ جبريل بالوحي إلى أخي هارون؛ ليعاونني ويصدقني فيما أقول، ويبين لهم ما أخاطبهم به، فهو أفصح مني نطقاً."));
        alshoaraaExes.add(new AlshoaraaEx("وَلَهُمْ عَلَيَّ ذَنْبٌ فَأَخَافُ أَنْ يَقْتُلُونِ (14)"));
        alshoaraaExes.add(new AlshoaraaEx("ولهم علي ذنب في قتل رجل منهم، وهو القبطي، فأخاف أن يقتلوني به."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ كَلَّا فَاذْهَبَا بِآيَاتِنَا إِنَّا مَعَكُمْ مُسْتَمِعُونَ (15)"));
        alshoaraaExes.add(new AlshoaraaEx("قال الله لموسى: كلا لن يقتلوك، وقد أجبت طلبك في هارون، فاذهبا بالمعجزات الدالة على صدقكما، إنا معكم بالعلم والحفظ والنصرة مستمعون."));
        alshoaraaExes.add(new AlshoaraaEx("فَأْتِيَا فِرْعَوْنَ فَقُولَا إِنَّا رَسُولُ رَبِّ الْعَالَمِينَ (16)"));
        alshoaraaExes.add(new AlshoaraaEx("فأتِيَا فرعون فقولا له: إنا مرسَلان إليك وإلى قومك من رب العالمين."));
        alshoaraaExes.add(new AlshoaraaEx("أَنْ أَرْسِلْ مَعَنَا بَنِي إِسْرَائِيلَ (17)"));
        alshoaraaExes.add(new AlshoaraaEx("أن اترك بني إسرائيل؛ ليذهبوا معنا."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ أَلَمْ نُرَبِّكَ فِينَا وَلِيدًا وَلَبِثْتَ فِينَا مِنْ عُمُرِكَ سِنِينَ (18)"));
        alshoaraaExes.add(new AlshoaraaEx("قال فرعون لموسى -ممتنًا عليه-: ألم نُرَبِّك في منازلنا صغيرًا، ومكثت في رعايتنا سنين من عُمُرك."));
        alshoaraaExes.add(new AlshoaraaEx("وَفَعَلْتَ فَعْلَتَكَ الَّتِي فَعَلْتَ وَأَنْتَ مِنَ الْكَافِرِينَ (19)"));
        alshoaraaExes.add(new AlshoaraaEx("وارتكبت جنايةً بقتلك رجلا من قومي حين ضربته ودفعته، وأنت من الجاحدين نعمتي المنكرين ربوبيتي؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ فَعَلْتُهَا إِذًا وَأَنَا مِنَ الضَّالِّينَ (20)"));
        alshoaraaExes.add(new AlshoaraaEx("قال موسى مجيبًا لفرعون: فعلتُ ما ذكرتَ قبل أن يوحي الله إلي، ويبعثني رسولا."));
        alshoaraaExes.add(new AlshoaraaEx("فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِي رَبِّي حُكْمًا وَجَعَلَنِي مِنَ الْمُرْسَلِينَ (21)"));
        alshoaraaExes.add(new AlshoaraaEx("فخرجت من بينكم فارًّا إلى «مدين» ، لمَّا خفت أن تقتلوني بما فعلتُ من غير عَمْد، فوهب لي ربي تفضلا منه النبوة والعلم، وجعلني من المرسلين."));
        alshoaraaExes.add(new AlshoaraaEx("وَتِلْكَ نِعْمَةٌ تَمُنُّهَا عَلَيَّ أَنْ عَبَّدْتَ بَنِي إِسْرَائِيلَ (22)"));
        alshoaraaExes.add(new AlshoaraaEx("أو تلك التربية في بيتك تَعُدُّها نعمة منك عليَّ، وقد جعلت بني إسرائيل عبيدًا تذبح أبناءهم وتستبقي نساءهم للخدمة والامتهان؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ فِرْعَوْنُ وَمَا رَبُّ الْعَالَمِينَ (23)"));
        alshoaraaExes.add(new AlshoaraaEx("قال فرعون لموسى: وما رب العالمين الذي تدَّعي أنك رسوله؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ رَبُّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا إِنْ كُنْتُمْ مُوقِنِينَ (24)"));
        alshoaraaExes.add(new AlshoaraaEx("قال موسى: هو مالك ومدبر السموات والأرض وما بينهما، إن كنتم موقنين بذلك، فآمِنوا."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ لِمَنْ حَوْلَهُ أَلَا تَسْتَمِعُونَ (25)"));
        alshoaraaExes.add(new AlshoaraaEx("قال فرعون لمن حوله مِن أشراف قومه: ألا تسمعون مقالة موسى العجيبة بوجود رب سواي؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ رَبُّكُمْ وَرَبُّ آبَائِكُمُ الْأَوَّلِينَ (26)"));
        alshoaraaExes.add(new AlshoaraaEx("قال موسى: الرب الذي أدعوكم إليه هو الذي خلقكم وخلق آباءكم الأولين، فكيف تعبدون مَن هو مخلوق مثلكم، وله آباء قد فنوا كآبائكم؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ إِنَّ رَسُولَكُمُ الَّذِي أُرْسِلَ إِلَيْكُمْ لَمَجْنُونٌ (27)"));
        alshoaraaExes.add(new AlshoaraaEx("قال فرعون لخاصته يستثير غضبهم؛ لتكذيب موسى إياه: إن رسولكم الذي أرسل إليكم لمجنون، يتكلم كلامًا لا يُعْقَل!"));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَا إِنْ كُنْتُمْ تَعْقِلُونَ (28)"));
        alshoaraaExes.add(new AlshoaraaEx("قال موسى: رب المشرق والمغرب وما بينهما وما يكون فيهما من نور وظلمة، وهذا يستوجب الإيمان به وحده إن كنتم من أهل العقل والتدبر!"));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ لَئِنِ اتَّخَذْتَ إِلَهًا غَيْرِي لَأَجْعَلَنَّكَ مِنَ الْمَسْجُونِينَ (29)"));
        alshoaraaExes.add(new AlshoaraaEx("قال فرعون لموسى مهددًا له: لئن اتخذت إلهًا غيري لأسجننك مع مَن سجنت."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ أَوَلَوْ جِئْتُكَ بِشَيْءٍ مُبِينٍ (30)"));
        alshoaraaExes.add(new AlshoaraaEx("قال موسى: أتجعلني من المسجونين، ولو جئتك ببرهان قاطع يتبين منه صدقي؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ فَأْتِ بِهِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ (31)"));
        alshoaraaExes.add(new AlshoaraaEx("قال فرعون: فأت به إن كنت من الصادقين في دعواك."));
        alshoaraaExes.add(new AlshoaraaEx("فَأَلْقَى عَصَاهُ فَإِذَا هِيَ ثُعْبَانٌ مُبِينٌ (32)"));
        alshoaraaExes.add(new AlshoaraaEx("فألقى موسى عصاه فتحولت ثعبانًا حقيقيًا، ليس تمويهًا كما يفعل السحرة."));
        alshoaraaExes.add(new AlshoaraaEx("وَنَزَعَ يَدَهُ فَإِذَا هِيَ بَيْضَاءُ لِلنَّاظِرِينَ (33)"));
        alshoaraaExes.add(new AlshoaraaEx("وأخرج يده مِن جيبه فإذا هي بيضاء كالثلج من غير برص، تَبْهَر الناظرين."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ لِلْمَلَإِ حَوْلَهُ إِنَّ هَذَا لَسَاحِرٌ عَلِيمٌ (34)"));
        alshoaraaExes.add(new AlshoaraaEx("قال فرعون لأشراف قومه خشية أن يؤمنوا: إن موسى لَساحر ماهر."));
        alshoaraaExes.add(new AlshoaraaEx("يُرِيدُ أَنْ يُخْرِجَكُمْ مِنْ أَرْضِكُمْ بِسِحْرِهِ فَمَاذَا تَأْمُرُونَ (35)"));
        alshoaraaExes.add(new AlshoaraaEx("يريد أن يخرجكم بسحره من أرضكم، فأي شيء تشيرون به في شأنه أتبع رأيكم فيه؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالُوا أَرْجِهْ وَأَخَاهُ وَابْعَثْ فِي الْمَدَائِنِ حَاشِرِينَ (36)"));
        alshoaraaExes.add(new AlshoaraaEx("قال له قومه: أخِّر أمر موسى وهارون، وأرسِلْ في المدائن جندًا جامعين للسحرة."));
        alshoaraaExes.add(new AlshoaraaEx("يَأْتُوكَ بِكُلِّ سَحَّارٍ عَلِيمٍ (37)"));
        alshoaraaExes.add(new AlshoaraaEx("يأتوك بكلِّ مَن أجاد السحر، وتفوَّق في معرفته."));
        alshoaraaExes.add(new AlshoaraaEx("فَجُمِعَ السَّحَرَةُ لِمِيقَاتِ يَوْمٍ مَعْلُومٍ (38)"));
        alshoaraaExes.add(new AlshoaraaEx("فَجُمع السحرة، وحُدِّد لهم وقت معلوم، هو وقت الضحى من يوم الزينة الذي يتفرغون فيه من أشغالهم، ويجتمعون ويتزيَّنون؛ وذلك للاجتماع بموسى."));
        alshoaraaExes.add(new AlshoaraaEx("وَقِيلَ لِلنَّاسِ هَلْ أَنْتُمْ مُجْتَمِعُونَ (39)"));
        alshoaraaExes.add(new AlshoaraaEx("وحُثَّ الناس على الاجتماع; أملا في أن تكون الغلبة للسحرة."));
        alshoaraaExes.add(new AlshoaraaEx("لَعَلَّنَا نَتَّبِعُ السَّحَرَةَ إِنْ كَانُوا هُمُ الْغَالِبِينَ (40)"));
        alshoaraaExes.add(new AlshoaraaEx("إننا نطمع أن تكون الغلبة للسحرة، فنثبت على ديننا."));
        alshoaraaExes.add(new AlshoaraaEx("فَلَمَّا جَاءَ السَّحَرَةُ قَالُوا لِفِرْعَوْنَ أَئِنَّ لَنَا لَأَجْرًا إِنْ كُنَّا نَحْنُ الْغَالِبِينَ (41)"));
        alshoaraaExes.add(new AlshoaraaEx("فلما جاء السحرة فرعون قالوا له: أإن لنا لأجرًا مِن مال أو جاه، إنْ كنا نحن الغالبين لموسى؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ نَعَمْ وَإِنَّكُمْ إِذًا لَمِنَ الْمُقَرَّبِينَ (42)"));
        alshoaraaExes.add(new AlshoaraaEx("قال فرعون: نعم لكم عندي ما طلبتم مِن أجر، وإنكم حينئذ لمن المقربين لديَّ."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ لَهُمْ مُوسَى أَلْقُوا مَا أَنْتُمْ مُلْقُونَ (43)"));
        alshoaraaExes.add(new AlshoaraaEx("قال موسى للسحرة مريدًا إبطال سحرهم وإظهار أن ما جاء به ليس سحرًا: ألقوا ما تريدون إلقاءه من السحر."));
        alshoaraaExes.add(new AlshoaraaEx("فَأَلْقَوْا حِبَالَهُمْ وَعِصِيَّهُمْ وَقَالُوا بِعِزَّةِ فِرْعَوْنَ إِنَّا لَنَحْنُ الْغَالِبُونَ (44)"));
        alshoaraaExes.add(new AlshoaraaEx("فألقَوا حبالهم وعصيَّهم، وخُيِّل للناس أنها حيَّات تسعى، وأقسموا بعزة فرعون قائلين: إننا لنحن الغالبون."));
        alshoaraaExes.add(new AlshoaraaEx("فَأَلْقَى مُوسَى عَصَاهُ فَإِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَ (45)"));
        alshoaraaExes.add(new AlshoaraaEx("فألقى موسى عصاه، فإذا هي حية عظيمة، تبتلع ما صدر منهم من إفك وتزوير."));
        alshoaraaExes.add(new AlshoaraaEx("فَأُلْقِيَ السَّحَرَةُ سَاجِدِينَ (46)"));
        alshoaraaExes.add(new AlshoaraaEx("فلما شاهدوا ذلك، وعلموا أنه ليس من تمويه السحرة، آمنوا بالله وسجدوا له."));
        alshoaraaExes.add(new AlshoaraaEx("قَالُوا آمَنَّا بِرَبِّ الْعَالَمِينَ (47)"));
        alshoaraaExes.add(new AlshoaraaEx("وقالوا: آمنَّا برب العالمين."));
        alshoaraaExes.add(new AlshoaraaEx("رَبِّ مُوسَى وَهَارُونَ (48)"));
        alshoaraaExes.add(new AlshoaraaEx("رب موسى وهارون."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ آمَنْتُمْ لَهُ قَبْلَ أَنْ آذَنَ لَكُمْ إِنَّهُ لَكَبِيرُكُمُ الَّذِي عَلَّمَكُمُ السِّحْرَ فَلَسَوْفَ تَعْلَمُونَ لَأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُمْ مِنْ خِلَافٍ وَلَأُصَلِّبَنَّكُمْ أَجْمَعِينَ (49)"));
        alshoaraaExes.add(new AlshoaraaEx("قال فرعون للسحرة مستنكرًا: آمنتم لموسى بغير إذن مني، وقال موهمًا أنَّ فِعْل موسى سحر: إنه لكبيركم الذي علَّمكم السحر، فلسوف تعلمون ما ينزل بكم من عقاب: لأقطعنَّ أيديكم وأرجلكم من خلاف: بقطع اليد اليمنى والرجل اليسرى أو عكس ذلك، ولأصلبنَّكم أجمعين."));
        alshoaraaExes.add(new AlshoaraaEx("قَالُوا لَا ضَيْرَ إِنَّا إِلَى رَبِّنَا مُنْقَلِبُونَ (50)"));
        alshoaraaExes.add(new AlshoaraaEx("قال السحرة لفرعون: لا ضرر علينا فيما يلحقنا من عقاب الدنيا، إنا راجعون إلى ربنا فيعطينا النعيم المقيم."));
        alshoaraaExes.add(new AlshoaraaEx("إِنَّا نَطْمَعُ أَنْ يَغْفِرَ لَنَا رَبُّنَا خَطَايَانَا أَنْ كُنَّا أَوَّلَ الْمُؤْمِنِينَ (51)"));
        alshoaraaExes.add(new AlshoaraaEx("إنا نرجو أن يغفر لنا ربنا خطايانا من الشرك وغيره; لكوننا أول المؤمنين في قومك."));
        alshoaraaExes.add(new AlshoaraaEx("وَأَوْحَيْنَا إِلَى مُوسَى أَنْ أَسْرِ بِعِبَادِي إِنَّكُمْ مُتَّبَعُونَ (52)"));
        alshoaraaExes.add(new AlshoaraaEx("وأوحى الله إلى موسى عليه السلام: أَنْ سِرْ ليلا بمن آمن من بني إسرائيل؛ لأن فرعون وجنوده متبعوكم حتى لا يدركوكم قبل وصولكم إلى البحر."));
        alshoaraaExes.add(new AlshoaraaEx("فَأَرْسَلَ فِرْعَوْنُ فِي الْمَدَائِنِ حَاشِرِينَ (53)"));
        alshoaraaExes.add(new AlshoaraaEx("فأرسل فرعون جنده -حين بلغه مسير بني إسرائيل- يجمعون جيشه من مدائن مملكته."));
        alshoaraaExes.add(new AlshoaraaEx("إِنَّ هَؤُلَاءِ لَشِرْذِمَةٌ قَلِيلُونَ (54)"));
        alshoaraaExes.add(new AlshoaraaEx("قال فرعون: إن بني إسرائيل الذين فرُّوا مع موسى لَطائفة حقيرة قليلة العدد."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِنَّهُمْ لَنَا لَغَائِظُونَ (55)"));
        alshoaraaExes.add(new AlshoaraaEx("وإنهم لمالئون صدورنا غيظًا؛ حيث خالفوا ديننا، وخرجوا بغير إذننا."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِنَّا لَجَمِيعٌ حَاذِرُونَ (56)"));
        alshoaraaExes.add(new AlshoaraaEx("وإنا لجميع متيقظون مستعدون لهم."));
        alshoaraaExes.add(new AlshoaraaEx("فَأَخْرَجْنَاهُمْ مِنْ جَنَّاتٍ وَعُيُونٍ (57)"));
        alshoaraaExes.add(new AlshoaraaEx("فأخرج الله فرعون وقومه من أرض «مصر» ذات البساتين وعيون الماء."));
        alshoaraaExes.add(new AlshoaraaEx("وَكُنُوزٍ وَمَقَامٍ كَرِيمٍ (58)"));
        alshoaraaExes.add(new AlshoaraaEx("وخزائن المال والمنازل الحسان. وكما أخرجناهم."));
        alshoaraaExes.add(new AlshoaraaEx("كَذَلِكَ وَأَوْرَثْنَاهَا بَنِي إِسْرَائِيلَ (59)"));
        alshoaraaExes.add(new AlshoaraaEx("جعلنا هذه الديار من بعدهم لبني إسرائيل."));
        alshoaraaExes.add(new AlshoaraaEx("فَأَتْبَعُوهُمْ مُشْرِقِينَ (60)"));
        alshoaraaExes.add(new AlshoaraaEx("فلحق فرعون وجنده موسى ومَن معه وقت شروق الشمس."));
        alshoaraaExes.add(new AlshoaraaEx("فَلَمَّا تَرَاءَى الْجَمْعَانِ قَالَ أَصْحَابُ مُوسَى إِنَّا لَمُدْرَكُونَ (61)"));
        alshoaraaExes.add(new AlshoaraaEx("فلما رأى كل واحد من الفريقين الآخر قال أصحاب موسى: إنَّ جَمْعَ فرعون مُدْرِكنا ومهلكنا."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ كَلَّا إِنَّ مَعِيَ رَبِّي سَيَهْدِينِ (62)"));
        alshoaraaExes.add(new AlshoaraaEx("قال موسى لهم: كلا ليس الأمر كما ذكرتم فلن تُدْرَكوا; إن معي ربي بالنصر، سيهديني لما فيه نجاتي ونجاتكم."));
        alshoaraaExes.add(new AlshoaraaEx("فَأَوْحَيْنَا إِلَى مُوسَى أَنِ اضْرِبْ بِعَصَاكَ الْبَحْرَ فَانْفَلَقَ فَكَانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظِيمِ (63)"));
        alshoaraaExes.add(new AlshoaraaEx("فأوحينا إلى موسى أن اضرب بعصاك البحر، فضرب، فانفلق البحر إلى اثني عشر طريقًا بعدد قبائل بني إسرائيل، فكانت كل قطعة انفصلت من البحر كالجبل العظيم."));
        alshoaraaExes.add(new AlshoaraaEx("وَأَزْلَفْنَا ثَمَّ الْآخَرِينَ (64)"));
        alshoaraaExes.add(new AlshoaraaEx("وقرَّبْنا هناك فرعون وقومه حتى دخلوا البحر."));
        alshoaraaExes.add(new AlshoaraaEx("وَأَنْجَيْنَا مُوسَى وَمَنْ مَعَهُ أَجْمَعِينَ (65)"));
        alshoaraaExes.add(new AlshoaraaEx("وأنجينا موسى ومَن معه أجمعين. فاستمر البحر على انفلاقه حتى عبروا إلى البر."));
        alshoaraaExes.add(new AlshoaraaEx("ثُمَّ أَغْرَقْنَا الْآخَرِينَ (66)"));
        alshoaraaExes.add(new AlshoaraaEx("ثم أغرقنا فرعون ومن معه بإطباق البحر عليهم بعد أن دخلوا فيه متبعين موسى وقومه."));
        alshoaraaExes.add(new AlshoaraaEx("إِنَّ فِي ذَلِكَ لَآيَةً وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ (67)"));
        alshoaraaExes.add(new AlshoaraaEx("إن في ذلك الذي حدث لَعبرة عجيبة دالة على قدرة الله، وما صار أكثر أتباع فرعون مؤمنين مع هذه العلامة الباهرة."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ (68)"));
        alshoaraaExes.add(new AlshoaraaEx("وإن ربك لهو العزيز الرحيم، بعزته أهلك الكافرين المكذبين، وبرحمته نجَّى موسى ومَن معه أجمعين."));
        alshoaraaExes.add(new AlshoaraaEx("وَاتْلُ عَلَيْهِمْ نَبَأَ إِبْرَاهِيمَ (69)"));
        alshoaraaExes.add(new AlshoaraaEx("واقصص على الكافرين - أيها الرسول - خبر إبراهيم."));
        alshoaraaExes.add(new AlshoaraaEx("إِذْ قَالَ لِأَبِيهِ وَقَوْمِهِ مَا تَعْبُدُونَ (70)"));
        alshoaraaExes.add(new AlshoaraaEx("حين قال لأبيه وقومه: أي شيء تعبدونه؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالُوا نَعْبُدُ أَصْنَامًا فَنَظَلُّ لَهَا عَاكِفِينَ (71)"));
        alshoaraaExes.add(new AlshoaraaEx("قالوا: نعبد أصنامًا، فنَعْكُف على عبادتها."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ هَلْ يَسْمَعُونَكُمْ إِذْ تَدْعُونَ (72)"));
        alshoaraaExes.add(new AlshoaraaEx("قال إبراهيم منبهًا على فساد مذهبهم: هل يسمعون دعاءكم إذ تدعونهم."));
        alshoaraaExes.add(new AlshoaraaEx("أَوْ يَنْفَعُونَكُمْ أَوْ يَضُرُّونَ (73)"));
        alshoaraaExes.add(new AlshoaraaEx("أو يقدِّمون لكم نفعًا إذا عبدتموهم، أو يصيبونكم بضر إذا تركتم عبادتهم؟"));
        alshoaraaExes.add(new AlshoaraaEx("قَالُوا بَلْ وَجَدْنَا آبَاءَنَا كَذَلِكَ يَفْعَلُونَ (74)"));
        alshoaraaExes.add(new AlshoaraaEx("قالوا: لا يكون منهم شيء من ذلك، ولكننا وجدنا آباءنا يعبدونهم، فقلَّدناهم فيما كانوا يفعلون."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ أَفَرَأَيْتُمْ مَا كُنْتُمْ تَعْبُدُونَ (75)"));
        alshoaraaExes.add(new AlshoaraaEx("قال إبراهيم: أفأبصرتم بتدبر ما كنتم تعبدون من الأصنام التي لا تسمع ولا تنفع ولا تضر."));
        alshoaraaExes.add(new AlshoaraaEx("أَنْتُمْ وَآبَاؤُكُمُ الْأَقْدَمُونَ (76)"));
        alshoaraaExes.add(new AlshoaraaEx("أنتم وآباؤكم الأقدمون من قبلكم؟"));
        alshoaraaExes.add(new AlshoaraaEx("فَإِنَّهُمْ عَدُوٌّ لِي إِلَّا رَبَّ الْعَالَمِينَ (77)"));
        alshoaraaExes.add(new AlshoaraaEx("فإن ما تعبدونهم من دون الله أعداء لي، لكن رب العالمين ومالك أمرهم هو وحده الذي أعبده."));
        alshoaraaExes.add(new AlshoaraaEx("الَّذِي خَلَقَنِي فَهُوَ يَهْدِينِ (78)"));
        alshoaraaExes.add(new AlshoaraaEx("هو الذي خلقني في أحسن صورة فهو يرشدني إلى مصالح الدنيا والآخرة."));
        alshoaraaExes.add(new AlshoaraaEx("وَالَّذِي هُوَ يُطْعِمُنِي وَيَسْقِينِ (79)"));
        alshoaraaExes.add(new AlshoaraaEx("وهو الذي ينعم عليَّ بالطعام والشراب."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِذَا مَرِضْتُ فَهُوَ يَشْفِينِ (80)"));
        alshoaraaExes.add(new AlshoaraaEx("وإذا أصابني مرض فهو الذي يَشْفيني ويعافيني منه."));
        alshoaraaExes.add(new AlshoaraaEx("وَالَّذِي يُمِيتُنِي ثُمَّ يُحْيِينِ (81)"));
        alshoaraaExes.add(new AlshoaraaEx("وهو الذي يميتني في الدينا بقبض روحي، ثم يحييني يوم القيامة، لا يقدر على ذلك أحد سواه."));
        alshoaraaExes.add(new AlshoaraaEx("وَالَّذِي أَطْمَعُ أَنْ يَغْفِرَ لِي خَطِيئَتِي يَوْمَ الدِّينِ (82)"));
        alshoaraaExes.add(new AlshoaraaEx("والذي أطمع أن يتجاوز عن ذنبي يوم الجزاء."));
        alshoaraaExes.add(new AlshoaraaEx("رَبِّ هَبْ لِي حُكْمًا وَأَلْحِقْنِي بِالصَّالِحِينَ (83)"));
        alshoaraaExes.add(new AlshoaraaEx("قال إبراهيم داعيًا ربه: ربِّ امنحني العلم والفهم، وألحقني بالصالحين، واجمع بيني وبينهم في الجنة."));
        alshoaraaExes.add(new AlshoaraaEx("وَاجْعَلْ لِي لِسَانَ صِدْقٍ فِي الْآخِرِينَ (84)"));
        alshoaraaExes.add(new AlshoaraaEx("واجعل لي ثناء حسنًا وذكرًا جميلا في الذين يأتون بعدي إلى يوم القيامة."));
        alshoaraaExes.add(new AlshoaraaEx("وَاجْعَلْنِي مِنْ وَرَثَةِ جَنَّةِ النَّعِيمِ (85)"));
        alshoaraaExes.add(new AlshoaraaEx("واجعلني من عبادك الذين تورثهم نعيم الجنة."));
        alshoaraaExes.add(new AlshoaraaEx("وَاغْفِرْ لِأَبِي إِنَّهُ كَانَ مِنَ الضَّالِّينَ (86)"));
        alshoaraaExes.add(new AlshoaraaEx("وهذا دعاء من إبراهيم عليه السلام أن ينقذ الله أباه من الضلال إلى الهدى، فيغفر له ويتجاوز عنه، كما وعد إبراهيم أباه بالدعاء له، فلما تبيَّن له أنه مستمر في الكفر والشرك إلى أن يموت تبرَّأ منه."));
        alshoaraaExes.add(new AlshoaraaEx("وَلَا تُخْزِنِي يَوْمَ يُبْعَثُونَ (87)"));
        alshoaraaExes.add(new AlshoaraaEx("ولا تُلْحق بي الذل، يوم يخرج الناس من القبور للحساب والجزاء."));
        alshoaraaExes.add(new AlshoaraaEx("يَوْمَ لَا يَنْفَعُ مَالٌ وَلَا بَنُونَ (88)"));
        alshoaraaExes.add(new AlshoaraaEx("يوم لا ينفع المال والبنون أحدًا من العباد."));
        alshoaraaExes.add(new AlshoaraaEx("إِلَّا مَنْ أَتَى اللَّهَ بِقَلْبٍ سَلِيمٍ (89)"));
        alshoaraaExes.add(new AlshoaraaEx("إلا مَن أتى الله بقلب سليم من الكفر والنفاق والرذيلة."));
        alshoaraaExes.add(new AlshoaraaEx("وَأُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِينَ (90)"));
        alshoaraaExes.add(new AlshoaraaEx("وقُرِّبت الجنة للذين اجتنبوا الكفر والمعاصي، وأقبلوا على الله بالطاعة."));
        alshoaraaExes.add(new AlshoaraaEx("وَبُرِّزَتِ الْجَحِيمُ لِلْغَاوِينَ (91)"));
        alshoaraaExes.add(new AlshoaraaEx("وأُظهرت النار للكافرين الذين ضَلُّوا عن الهدى، وتجرَّؤوا على محارم الله وكذَّبوا رسله."));
        alshoaraaExes.add(new AlshoaraaEx("وَقِيلَ لَهُمْ أَيْنَ مَا كُنْتُمْ تَعْبُدُونَ (92)"));
        alshoaraaExes.add(new AlshoaraaEx("وقيل لهم توبيخًا: أين آلهتكم التي كنتم تعبدونها مِن دون الله، وتزعمون أنها تشفع لكم اليوم؟"));
        alshoaraaExes.add(new AlshoaraaEx("مِنْ دُونِ اللَّهِ هَلْ يَنْصُرُونَكُمْ أَوْ يَنْتَصِرُونَ (93)"));
        alshoaraaExes.add(new AlshoaraaEx("هل ينصرونكم، فيدفعون العذاب عنكم، أو ينتصرون بدفع العذاب عن أنفسهم؟ لا شيء من ذلك."));
        alshoaraaExes.add(new AlshoaraaEx("فَكُبْكِبُوا فِيهَا هُمْ وَالْغَاوُونَ (94)"));
        alshoaraaExes.add(new AlshoaraaEx("فجُمِعوا وألقُوا في جهنم، هم والذين أضلوهم."));
        alshoaraaExes.add(new AlshoaraaEx("وَجُنُودُ إِبْلِيسَ أَجْمَعُونَ (95)"));
        alshoaraaExes.add(new AlshoaraaEx("وأعوان إبليس الذين زيَّنوا لهم الشر، لم يُفْلِت منهم أحد."));
        alshoaraaExes.add(new AlshoaraaEx("قَالُوا وَهُمْ فِيهَا يَخْتَصِمُونَ (96)"));
        alshoaraaExes.add(new AlshoaraaEx("قالوا معترفين بخطئهم، وهم يتنازعون في جهنم مع مَن أضلوهم."));
        alshoaraaExes.add(new AlshoaraaEx("تَاللَّهِ إِنْ كُنَّا لَفِي ضَلَالٍ مُبِينٍ (97)"));
        alshoaraaExes.add(new AlshoaraaEx("تالله إننا كنا في الدنيا في ضلال واضح لا خفاء فيه."));
