        alshoaraaExes.add(new AlshoaraaEx("الَّذِينَ يُفْسِدُونَ فِي الْأَرْضِ وَلَا يُصْلِحُونَ (152)"));
        alshoaraaExes.add(new AlshoaraaEx("الذين دأبوا على الإفساد في الأرض إفسادًا لا إصلاح فيه."));
        alshoaraaExes.add(new AlshoaraaEx("قَالُوا إِنَّمَا أَنْتَ مِنَ الْمُسَحَّرِينَ (153)"));
        alshoaraaExes.add(new AlshoaraaEx("قالت ثمود لنبيها صالح: ما أنت إلا من الذين سُحروا سِحْرًا كثيرًا، حتى غلب السحر على عقلك."));
        alshoaraaExes.add(new AlshoaraaEx("مَا أَنْتَ إِلَّا بَشَرٌ مِثْلُنَا فَأْتِ بِآيَةٍ إِنْ كُنْتَ مِنَ الصَّادِقِينَ (154)"));
        alshoaraaExes.add(new AlshoaraaEx("ما أنت إلا فرد مماثل لنا في البشرية من بني آدم، فكيف تتميز علينا بالرسالة؟ فأت بحجة واضحة تدل على ثبوت رسالتك، إن كنت صادقًا في دعواك أن الله أرسلك إلينا."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ هَذِهِ نَاقَةٌ لَهَا شِرْبٌ وَلَكُمْ شِرْبُ يَوْمٍ مَعْلُومٍ (155)"));
        alshoaraaExes.add(new AlshoaraaEx("قال لهم صالح -وقد أتاهم بناقة أخرجها الله له من الصخرة-: هذه ناقة الله لها نصيب من الماء في يوم معلوم، ولكم نصيب منه في يوم آخر. ليس لكم أن تشربوا في اليوم الذي هو نصيبها، ولا هي تشرب في اليوم الذي هو نصيبكم"));
        alshoaraaExes.add(new AlshoaraaEx("وَلَا تَمَسُّوهَا بِسُوءٍ فَيَأْخُذَكُمْ عَذَابُ يَوْمٍ عَظِيمٍ (156)"));
        alshoaraaExes.add(new AlshoaraaEx("ولا تنالوها بشيء مما يسوءها كضَرْبٍ أو قتل أو نحو ذلك، فيهلككم الله بعذابِ يومٍ تعظم شدته؛ بسبب ما يقع فيه من الهول والشدة."));
        alshoaraaExes.add(new AlshoaraaEx("فَعَقَرُوهَا فَأَصْبَحُوا نَادِمِينَ (157)"));
        alshoaraaExes.add(new AlshoaraaEx("فنحروا الناقة، فأصبحوا متحسرين على ما فعلوا لَمَّا أيقنوا بالعذاب، فلم ينفعهم ندمهم."));
        alshoaraaExes.add(new AlshoaraaEx("فَأَخَذَهُمُ الْعَذَابُ إِنَّ فِي ذَلِكَ لَآيَةً وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ (158)"));
        alshoaraaExes.add(new AlshoaraaEx("فنزل بهم عذاب الله الذي توعدهم به صالح عليه السلام، فأهلكهم. إن في إهلاك ثمود لَعبرة لمن اعتبر بهذا المصير، وما كان أكثرهم مؤمنين."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ (159)"));
        alshoaraaExes.add(new AlshoaraaEx("وإن ربك لهو العزيز القاهر المنتقم من أعدائه المكذبين، الرحيم بمن آمن من خلقه."));
        alshoaraaExes.add(new AlshoaraaEx("كَذَّبَتْ قَوْمُ لُوطٍ الْمُرْسَلِينَ (160)"));
        alshoaraaExes.add(new AlshoaraaEx("كَذَّبت قوم لوط برسالته، فكانوا بهذا مكذبين لسائر رسل الله؛ لأن ما جاؤوا به من التوحيد وأصول الشرائع واحد."));
        alshoaraaExes.add(new AlshoaraaEx("إِذْ قَالَ لَهُمْ أَخُوهُمْ لُوطٌ أَلَا تَتَّقُونَ (161)"));
        alshoaraaExes.add(new AlshoaraaEx("إذ قال لهم أخوهم لوط: ألا تخشون عذاب الله؟"));
        alshoaraaExes.add(new AlshoaraaEx("إِنِّي لَكُمْ رَسُولٌ أَمِينٌ (162)"));
        alshoaraaExes.add(new AlshoaraaEx("إني رسول من ربكم، أمين على تبليغ رسالته إليكم."));
        alshoaraaExes.add(new AlshoaraaEx("فَاتَّقُوا اللَّهَ وَأَطِيعُونِ (163)"));
        alshoaraaExes.add(new AlshoaraaEx("فاحذروا عقاب الله على تكذيبكم رسوله، واتبعوني فيما دعوتكم إليه."));
        alshoaraaExes.add(new AlshoaraaEx("وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِيَ إِلَّا عَلَى رَبِّ الْعَالَمِينَ (164)"));
        alshoaraaExes.add(new AlshoaraaEx("وما أسألكم على دعوتي لهدايتكم أيَّ أجر، ما أجري إلا على رب العالمين."));
        alshoaraaExes.add(new AlshoaraaEx("أَتَأْتُونَ الذُّكْرَانَ مِنَ الْعَالَمِينَ (165)"));
        alshoaraaExes.add(new AlshoaraaEx("أتنكحون الذكور مِن بني آدم."));
        alshoaraaExes.add(new AlshoaraaEx("وَتَذَرُونَ مَا خَلَقَ لَكُمْ رَبُّكُمْ مِنْ أَزْوَاجِكُمْ بَلْ أَنْتُمْ قَوْمٌ عَادُونَ (166)"));
        alshoaraaExes.add(new AlshoaraaEx("وتتركون ما خلق الله لاستمتاعكم وتناسلكم مِن أزواجكم؟ بل أنتم قوم -بهذه المعصية- متجاوزون ما أباحه الله لكم من الحلال إلى الحرام."));
        alshoaraaExes.add(new AlshoaraaEx("قَالُوا لَئِنْ لَمْ تَنْتَهِ يَالُوطُ لَتَكُونَنَّ مِنَ الْمُخْرَجِينَ (167)"));
        alshoaraaExes.add(new AlshoaraaEx("قال قوم لوط: لئن لم تترك يا لوط نَهْيَنا عن إتيان الذكور وتقبيح فعله، لتكونن من المطرودين من بلادنا."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ إِنِّي لِعَمَلِكُمْ مِنَ الْقَالِينَ (168)"));
        alshoaraaExes.add(new AlshoaraaEx("قال لوط لهم: إني لِعملكم الذي تعملونه من إتيان الذكور، لَمن المبغضين له بغضًا شديدًا."));
        alshoaraaExes.add(new AlshoaraaEx("رَبِّ نَجِّنِي وَأَهْلِي مِمَّا يَعْمَلُونَ (169)"));
        alshoaraaExes.add(new AlshoaraaEx("ثم دعا لوط ربه حينما يئس من استجابتهم له قائلا ربِّ أنقذني وأنقذ أهلي مما يعمله قومي مِن هذه المعصية القبيحة، ومِن عقوبتك التي ستصيبهم."));
        alshoaraaExes.add(new AlshoaraaEx("فَنَجَّيْنَاهُ وَأَهْلَهُ أَجْمَعِينَ (170)"));
        alshoaraaExes.add(new AlshoaraaEx("فنجيناه وأهل بيته والمستجيبين لدعوته أجمعين."));
        alshoaraaExes.add(new AlshoaraaEx("إِلَّا عَجُوزًا فِي الْغَابِرِينَ (171)"));
        alshoaraaExes.add(new AlshoaraaEx("إلا عجوزًا من أهله، وهي امرأته، لم تشاركهم في الإيمان، فكانت من الباقين في العذاب والهلاك."));
        alshoaraaExes.add(new AlshoaraaEx("ثُمَّ دَمَّرْنَا الْآخَرِينَ (172)"));
        alshoaraaExes.add(new AlshoaraaEx("ثم أهلكنا مَن عداهم من الكفرة أشدَّ إهلاك."));
        alshoaraaExes.add(new AlshoaraaEx("وَأَمْطَرْنَا عَلَيْهِمْ مَطَرًا فَسَاءَ مَطَرُ الْمُنْذَرِينَ (173)"));
        alshoaraaExes.add(new AlshoaraaEx("وأنزلنا عليهم حجارة من السماء كالمطر أهلكتهم، فقَبُحَ مطرُ من أنذرهم رسلهم ولم يستجيبوا لهم؛ فقد أُنزل بهم أشدُّ أنواع الهلاك والتدمير."));
        alshoaraaExes.add(new AlshoaraaEx("إِنَّ فِي ذَلِكَ لَآيَةً وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ (174)"));
        alshoaraaExes.add(new AlshoaraaEx("إن في ذلك العقاب الذي نزل بقوم لوط لَعبرة وموعظة، يتعظ بها المكذبون. وما كان أكثرهم مؤمنين."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ (175)"));
        alshoaraaExes.add(new AlshoaraaEx("وإن ربك لهو العزيز الغالب الذي يقهر المكذبين، الرحيم بعباده المؤمنين."));
        alshoaraaExes.add(new AlshoaraaEx("كَذَّبَ أَصْحَابُ الْأَيْكَةِ الْمُرْسَلِينَ (176)"));
        alshoaraaExes.add(new AlshoaraaEx("كذَّب أصحاب الأرض ذات الشجر الملتف رسولهم شعيبًا في رسالته، فكانوا بهذا مكذِّبين لجميع الرسالات."));
        alshoaraaExes.add(new AlshoaraaEx("إِذْ قَالَ لَهُمْ شُعَيْبٌ أَلَا تَتَّقُونَ (177)"));
        alshoaraaExes.add(new AlshoaraaEx("إذ قال لهم شعيب: ألا تخشون عقاب الله على شرككم ومعاصيكم؟"));
        alshoaraaExes.add(new AlshoaraaEx("إِنِّي لَكُمْ رَسُولٌ أَمِينٌ (178)"));
        alshoaraaExes.add(new AlshoaraaEx("إني مرسَل إليكم مِنَ الله لهدايتكم، حفيظ على ما أوحى الله به إليَّ من الرسالة."));
        alshoaraaExes.add(new AlshoaraaEx("فَاتَّقُوا اللَّهَ وَأَطِيعُونِ (179)"));
        alshoaraaExes.add(new AlshoaraaEx("فخافوا عقاب الله، واتبعوا ما دعوتكم إليه مِن هداية الله؛ لترشدوا."));
        alshoaraaExes.add(new AlshoaraaEx("وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِيَ إِلَّا عَلَى رَبِّ الْعَالَمِينَ (180)"));
        alshoaraaExes.add(new AlshoaraaEx("وما أطلب منكم على دعائي لكم إلى الإيمان بالله أيَّ جزاء، ما جزائي إلا على رب العالمين."));
        alshoaraaExes.add(new AlshoaraaEx("أَوْفُوا الْكَيْلَ وَلَا تَكُونُوا مِنَ الْمُخْسِرِينَ (181)"));
        alshoaraaExes.add(new AlshoaraaEx("قال لهم شعيب -وقد كانوا يُنْقِصون الكيل والميزان-: أتمُّوا الكيل للناس وافيًا لهم، ولا تكونوا ممن يُنْقِصون الناس حقوقهم."));
        alshoaraaExes.add(new AlshoaraaEx("وَزِنُوا بِالْقِسْطَاسِ الْمُسْتَقِيمِ (182)"));
        alshoaraaExes.add(new AlshoaraaEx("وَزِنوا بالميزان العدل المستقيم."));
        alshoaraaExes.add(new AlshoaraaEx("وَلَا تَبْخَسُوا النَّاسَ أَشْيَاءَهُمْ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ (183)"));
        alshoaraaExes.add(new AlshoaraaEx("ولا تنقصوا الناس شيئًا مِن حقوقهم في كيل أو وزن أو غير ذلك، ولا تكثروا في الأرض الفساد، بالشرك والقتل والنهب وتخويف الناس وارتكاب المعاصي."));
        alshoaraaExes.add(new AlshoaraaEx("وَاتَّقُوا الَّذِي خَلَقَكُمْ وَالْجِبِلَّةَ الْأَوَّلِينَ (184)"));
        alshoaraaExes.add(new AlshoaraaEx("واحذروا عقوبة الله الذي خلقكم وخلق الأمم المتقدمة عليكم."));
        alshoaraaExes.add(new AlshoaraaEx("قَالُوا إِنَّمَا أَنْتَ مِنَ الْمُسَحَّرِينَ (185)"));
        alshoaraaExes.add(new AlshoaraaEx("قالوا: إنما أنت -يا شعيب- مِنَ الذين أصابهم السحر إصابة شديدة، فذهب بعقولهم."));
        alshoaraaExes.add(new AlshoaraaEx("وَمَا أَنْتَ إِلَّا بَشَرٌ مِثْلُنَا وَإِنْ نَظُنُّكَ لَمِنَ الْكَاذِبِينَ (186)"));
        alshoaraaExes.add(new AlshoaraaEx("وما أنت إلا واحد مثلنا في البشرية، فكيف تختص دوننا بالرسالة؟ وإن أكبر ظننا أنك من الكاذبين فيما تدَّعيه من الرسالة."));
        alshoaraaExes.add(new AlshoaraaEx("فَأَسْقِطْ عَلَيْنَا كِسَفًا مِنَ السَّمَاءِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ (187)"));
        alshoaraaExes.add(new AlshoaraaEx("فإن كنت صادقًا في دعوى النبوة، فادع الله أن يسقط علينا قطع عذاب من السماء تستأصلنا."));
        alshoaraaExes.add(new AlshoaraaEx("قَالَ رَبِّي أَعْلَمُ بِمَا تَعْمَلُونَ (188)"));
        alshoaraaExes.add(new AlshoaraaEx("قال لهم شعيب: ربي أعلم بما تعملونه مِنَ الشرك والمعاصي، وبما تستوجبونه من العقاب."));
        alshoaraaExes.add(new AlshoaraaEx("فَكَذَّبُوهُ فَأَخَذَهُمْ عَذَابُ يَوْمِ الظُّلَّةِ إِنَّهُ كَانَ عَذَابَ يَوْمٍ عَظِيمٍ (189)"));
        alshoaraaExes.add(new AlshoaraaEx("فاستمَرُّوا على تكذيبه، فأصابهم الحر الشديد، وصاروا يبحثون عن ملاذ يستظلون به، فأظلتهم سحابة، وجدوا لها بردًا ونسيمًا، فلما اجتمعوا تحتها، التهبت عليهم نارًا فأحرقتهم، فكان هلاكهم جميعًا في يوم شديد الهول."));
        alshoaraaExes.add(new AlshoaraaEx("إِنَّ فِي ذَلِكَ لَآيَةً وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ (190)"));
        alshoaraaExes.add(new AlshoaraaEx("إن في ذلك العقاب الذي نزل بهم، لَدلالة واضحة على قدرة الله في مؤاخذة المكذبين، وعبرة لمن يعتبر، وما كان أكثرهم مؤمنين متعظين بذلك."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ (191)"));
        alshoaraaExes.add(new AlshoaraaEx("وإن ربك - أيها الرسول - لهو العزيز في نقمته ممن انتقم منه من أعدائه، الرحيم بعباده الموحدين."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِنَّهُ لَتَنْزِيلُ رَبِّ الْعَالَمِينَ (192)"));
        alshoaraaExes.add(new AlshoaraaEx("وإن هذا القرآن الذي ذُكِرَتْ فيه هذه القصص الصادقة، لَمنزَّل مِن خالق الخلق، ومالك الأمر كله."));
        alshoaraaExes.add(new AlshoaraaEx("نَزَلَ بِهِ الرُّوحُ الْأَمِينُ (193)"));
        alshoaraaExes.add(new AlshoaraaEx("نزل به جبريل الأمين، فتلاه عليك - أيها الرسول -."));
        alshoaraaExes.add(new AlshoaraaEx("عَلَى قَلْبِكَ لِتَكُونَ مِنَ الْمُنْذِرِينَ (194)"));
        alshoaraaExes.add(new AlshoaraaEx("حتى وعيته بقلبك حفظًا وفهمًا؛ لتكون مِن رسل الله الذين يخوِّفون قومهم عقاب الله، فتنذر بهذا التنزيل الإنس والجن أجمعين."));
        alshoaraaExes.add(new AlshoaraaEx("بِلِسَانٍ عَرَبِيٍّ مُبِينٍ (195)"));
        alshoaraaExes.add(new AlshoaraaEx("نزل به جبريل عليك بلغة عربية واضحة المعنى، ظاهرة الدلالة، فيما يحتاجون إليه في إصلاح شؤون دينهم ودنياهم."));
        alshoaraaExes.add(new AlshoaraaEx("وَإِنَّهُ لَفِي زُبُرِ الْأَوَّلِينَ (196)"));
        alshoaraaExes.add(new AlshoaraaEx("وإنَّ ذِكْرَ هذا القرآن لَمثبتٌ في كتب الأنبياء السابقين، قد بَشَّرَتْ به وصَدَّقَتْه."));
        alshoaraaExes.add(new AlshoaraaEx("أَوَلَمْ يَكُنْ لَهُمْ آيَةً أَنْ يَعْلَمَهُ عُلَمَاءُ بَنِي إِسْرَائِيلَ (197)"));
        alshoaraaExes.add(new AlshoaraaEx("أو لم يَكْفِ هؤلاء -في الدلالة على أنك رسول الله، وأن القرآن حق- عِلْمُ علماء بني إسرائيل صحة ذلك، ومَن آمن منهم كعبد الله بن سلام؟"));
        alshoaraaExes.add(new AlshoaraaEx("وَلَوْ نَزَّلْنَاهُ عَلَى بَعْضِ الْأَعْجَمِينَ (198)"));
        alshoaraaExes.add(new AlshoaraaEx("ولو نَزَّلنا القرآن على بعض الذين لا يتكلمون بالعربية."));
        alshoaraaExes.add(new AlshoaraaEx("فَقَرَأَهُ عَلَيْهِمْ مَا كَانُوا بِهِ مُؤْمِنِينَ (199)"));
        alshoaraaExes.add(new AlshoaraaEx("رأه على كفار قريش قراءة عربية صحيحة، لكفروا به أيضًا، وانتحلوا لجحودهم عذرًا."));
        alshoaraaExes.add(new AlshoaraaEx("كَذَلِكَ سَلَكْنَاهُ فِي قُلُوبِ الْمُجْرِمِينَ (200)"));
        alshoaraaExes.add(new AlshoaraaEx("كذلك أدخلنا في قلوب المجرمين جحود القرآن، وصار متمكنًا فيها؛ وذلك بسبب ظلمهم وإجرامهم."));
        alshoaraaExes.add(new AlshoaraaEx("لَا يُؤْمِنُونَ بِهِ حَتَّى يَرَوُا الْعَذَابَ الْأَلِيمَ (201)"));
        alshoaraaExes.add(new AlshoaraaEx("فلا سبيل إلى أن يتغيروا عمَّا هم عليه من إنكار القرآن، حتى يعاينوا العذاب الشديد الذي وُعِدوا به."));
        alshoaraaExes.add(new AlshoaraaEx("فَيَأْتِيَهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ (202)"));
        alshoaraaExes.add(new AlshoaraaEx("فينزل بهم العذاب فجأة، وهم لا يعلمون قبل ذلك بمجيئه."));
        alshoaraaExes.add(new AlshoaraaEx("فَيَقُولُوا هَلْ نَحْنُ مُنْظَرُونَ (203)"));
        alshoaraaExes.add(new AlshoaraaEx("فيقولون عند مفاجأتهم به تحسُّرًا على ما فاتهم من الإيمان: هل نحن مُمْهَلون مُؤخَّرون؛ لنتوب إلى الله مِن شركنا، ونستدرك ما فاتنا؟"));
        alshoaraaExes.add(new AlshoaraaEx("أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ (204)"));
        alshoaraaExes.add(new AlshoaraaEx("أَغَرَّ هؤلاء إمهالي، فيستعجلون نزول العذاب عليهم من السماء؟"));
        alshoaraaExes.add(new AlshoaraaEx("أَفَرَأَيْتَ إِنْ مَتَّعْنَاهُمْ سِنِينَ (205)"));
        alshoaraaExes.add(new AlshoaraaEx("أفعلمت - أيها الرسول - إن مَتَّعناهم بالحياة سنين طويلة بتأخير آجالهم."));
        alshoaraaExes.add(new AlshoaraaEx("ثُمَّ جَاءَهُمْ مَا كَانُوا يُوعَدُونَ (206)"));
        alshoaraaExes.add(new AlshoaraaEx("ثم نزل بهم العذاب الموعود؟"));
        alshoaraaExes.add(new AlshoaraaEx("مَا أَغْنَى عَنْهُمْ مَا كَانُوا يُمَتَّعُونَ (207)"));
        alshoaraaExes.add(new AlshoaraaEx("ما أغنى عنهم تمتعهم بطول العمر، وطيب العيش، إذا لم يتوبوا من شركهم؟ فعذاب الله واقع بهم عاجلا أم آجلا."));
        alshoaraaExes.add(new AlshoaraaEx("وَمَا أَهْلَكْنَا مِنْ قَرْيَةٍ إِلَّا لَهَا مُنْذِرُونَ (208)"));
        alshoaraaExes.add(new AlshoaraaEx("وما أهلكنا مِن قرية من القرى في الأمم جميعًا، إلا بعد أن نرسل إليهم رسلا ينذرونهم."));
        alshoaraaExes.add(new AlshoaraaEx("ذِكْرَى وَمَا كُنَّا ظَالِمِينَ (209)"));
        alshoaraaExes.add(new AlshoaraaEx("تذكرة لهم وتنبيهًا على ما فيه نجاتهم، وما كنا ظالمين فنعذب أمة قبل أن نرسل إليها رسولا."));
        alshoaraaExes.add(new AlshoaraaEx("وَمَا تَنَزَّلَتْ بِهِ الشَّيَاطِينُ (210)"));
        alshoaraaExes.add(new AlshoaraaEx("وما تَنَزَّلَتْ بالقرآن على محمد الشياطين -كما يزعم الكفرة-."));
        alshoaraaExes.add(new AlshoaraaEx("وَمَا يَنْبَغِي لَهُمْ وَمَا يَسْتَطِيعُونَ (211)"));
        alshoaraaExes.add(new AlshoaraaEx("ولا يصح منهم ذلك، وما يستطيعونه."));
        alshoaraaExes.add(new AlshoaraaEx("إِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُولُونَ (212)"));
        alshoaraaExes.add(new AlshoaraaEx("لأنهم عن استماع القرآن من السماء محجوبون مرجومون بالشهب."));
        alshoaraaExes.add(new AlshoaraaEx("فَلَا تَدْعُ مَعَ اللَّهِ إِلَهًا آخَرَ فَتَكُونَ مِنَ الْمُعَذَّبِينَ (213)"));
        alshoaraaExes.add(new AlshoaraaEx("فلا تعبد مع الله معبودًا غيره، فينزل بك من العذاب ما نزل بهؤلاء الذين عبدوا مع الله غيره."));
        alshoaraaExes.add(new AlshoaraaEx("وَأَنْذِرْ عَشِيرَتَكَ الْأَقْرَبِينَ (214)"));
        alshoaraaExes.add(new AlshoaraaEx("وحذِّر - أيها الرسول - الأقرب فالأقرب مِن قومك، مِن عذابنا، أن ينزل بهم."));
        alshoaraaExes.add(new AlshoaraaEx("وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِينَ (215)"));
        alshoaraaExes.add(new AlshoaraaEx("وأَلِنْ جانبك وكلامك تواضعًا ورحمة لمن ظهر لك منه إجابة دعوتك."));
        alshoaraaExes.add(new AlshoaraaEx("فَإِنْ عَصَوْكَ فَقُلْ إِنِّي بَرِيءٌ مِمَّا تَعْمَلُونَ (216)"));
        alshoaraaExes.add(new AlshoaraaEx("فإن خالفوا أمرك ولم يتبعوك، فتبرَّأ من أعمالهم، وما هم عليه من الشرك والضلال."));
        alshoaraaExes.add(new AlshoaraaEx("وَتَوَكَّلْ عَلَى الْعَزِيزِ الرَّحِيمِ (217)"));
        alshoaraaExes.add(new AlshoaraaEx("وفَوِّضْ أمرك إلى الله العزيز الذي لا يغالَب ولا يُقْهَر، الرحيم الذي لا يخذل أولياءه."));
        alshoaraaExes.add(new AlshoaraaEx("الَّذِي يَرَاكَ حِينَ تَقُومُ (218)"));
        alshoaraaExes.add(new AlshoaraaEx("وهو الذي يراك حين تقوم للصلاة وحدك في جوف الليل."));
        alshoaraaExes.add(new AlshoaraaEx("وَتَقَلُّبَكَ فِي السَّاجِدِينَ (219)"));
        alshoaraaExes.add(new AlshoaraaEx("ويرى تقلُّبك مع الساجدين في صلاتهم معك قائمًا وراكعًا وساجدًا وجالسًا."));
        alshoaraaExes.add(new AlshoaraaEx("إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ (220)"));
        alshoaraaExes.add(new AlshoaraaEx("إنه -سبحانه- هو السميع لتلاوتك وذكرك، العليم بنيتك وعملك."));
        alshoaraaExes.add(new AlshoaraaEx("هَلْ أُنَبِّئُكُمْ عَلَى مَنْ تَنَزَّلُ الشَّيَاطِينُ (221)"));
        alshoaraaExes.add(new AlshoaraaEx("هل أخبركم -أيها الناس- على مَن تنزَّل الشياطين؟"));
        alshoaraaExes.add(new AlshoaraaEx("تَنَزَّلُ عَلَى كُلِّ أَفَّاكٍ أَثِيمٍ (222)"));
        alshoaraaExes.add(new AlshoaraaEx("تتنزل على كل كذَّاب كثير الآثام من الكهنة."));
        alshoaraaExes.add(new AlshoaraaEx("يُلْقُونَ السَّمْعَ وَأَكْثَرُهُمْ كَاذِبُونَ (223)"));
        alshoaraaExes.add(new AlshoaraaEx("يَسْتَرِقُ الشياطين السمع، يتخطفونه من الملأ الأعلى، فيلقونه إلى الكهان، ومَن جرى مجراهم مِنَ الفسقة، وأكثر هؤلاء كاذبون، يَصْدُق أحدهم في كلمة، فيزيد فيها أكثر مِن مائة كذبة."));
        alshoaraaExes.add(new AlshoaraaEx("وَالشُّعَرَاءُ يَتَّبِعُهُمُ الْغَاوُونَ (224)"));
        alshoaraaExes.add(new AlshoaraaEx("والشعراء يقوم شعرهم على الباطل والكذب، ويجاريهم الضالون الزائغون مِن أمثالهم."));
        alshoaraaExes.add(new AlshoaraaEx("أَلَمْ تَرَ أَنَّهُمْ فِي كُلِّ وَادٍ يَهِيمُونَ (225)"));
        alshoaraaExes.add(new AlshoaraaEx("ألم تر - أيها النبي - أنهم يذهبون كالهائم على وجهه، يخوضون في كل فن مِن فنون الكذب والزور وتمزيق الأعراض والطعن في الأنساب وتجريح النساء العفائف."));
        alshoaraaExes.add(new AlshoaraaEx("وَأَنَّهُمْ يَقُولُونَ مَا لَا يَفْعَلُونَ (226)"));
        alshoaraaExes.add(new AlshoaraaEx("وأنهم يقولون ما لا يفعلون، يبالغون في مدح أهل الباطل، وينتقصون أهل الحق؟"));
        alshoaraaExes.add(new AlshoaraaEx("إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَذَكَرُوا اللَّهَ كَثِيرًا وَانْتَصَرُوا مِنْ بَعْدِ مَا ظُلِمُوا وَسَيَعْلَمُ الَّذِينَ ظَلَمُوا أَيَّ مُنْقَلَبٍ يَنْقَلِبُونَ (227)"));
        alshoaraaExes.add(new AlshoaraaEx("استثنى الله من الشعراءِ الشعراءَ الذين اهتدَوْا بالإيمان وعملوا الصالحات، وأكثروا مِن ذِكْر الله فقالوا الشعر في توحيد الله -سبحانه- والثناء عليه جلَّ ذكره، والدفاع عن رسوله محمد صلى الله عليه وسلم، وتكلموا بالحكمة والموعظة والآداب الحسنة، وانتصروا للإسلام، يهجون مَن يهجوه أو يهجو رسوله، ردًّا على الشعراء الكافرين. وسيعلم الذين ظلموا أنفسهم بالشرك والمعاصي، وظلموا غيرهم بغمط حقوقهم، أو الاعتداء عليهم، أو بالتُّهم الباطلة، أي مرجع من مراجع الشر والهلاك يرجعون إليه؟ إنَّه منقلب سوء، نسأل الله السلامة والعافية."));

